import React from 'react';
import './styles/styles.scss';
import logo from './green-logo.svg';

class App extends React.Component {
  constructor() {
    super();
    this.validateForm = this.validateForm.bind(this);

    // this.validateForm();
  }

  emailInput = React.createRef();

  validateForm() {
    const emailInput = this.emailInput.current.value;
    console.log(emailInput, 'please');
    console.log('business');
  }

  render() {
    this.validateForm();

    return (
      <div className="login">
        <img src={logo} alt='Green Energy logo' />
        <form>
          <h1>Example login screen</h1>
          <p>Getting started with Green</p>
          <div className="input-container">
            <label htmlFor="email">Email Address</label>
            <input
            type="email"
            id="email"
            ref={this.emailInput}
            required
            />
          </div>
          <div className="checkbox-container">
            <label htmlFor="remember">Remember this device</label>
            <input
            type="checkbox"
            id="remember"
            />
            </div>
          <button
            type="submit"
            disabled>Sign In</button>
        </form>
      </div>
    );
  }
}

export default App;
